	public class ViewModel : ViewModelBase
	{
		private ObservableCollection<Movie> allMovies;

		public ViewModel()
		{
			this.studios = this.GetStudios();
			this.allMovies = this.GetMovies();
		}

		private ObservableCollection<string> studios;
		public ObservableCollection<string> Studios { get { return this.studios; } }

		private string selectedStudio;
		public string SelectedStudio
		{
			get
			{
				if (string.IsNullOrEmpty(this.selectedStudio))
				{
					this.selectedStudio = "Paramount Pictures";
				}
				return selectedStudio;
			}
			set
			{
				selectedStudio = value;
				if (this.movies != null)
				{
					this.movies.Clear();
					this.movies = this.GetMoviesForStudio(value);
				}
				OnPropertyChanged(() => this.SelectedStudio);
				OnPropertyChanged(() => this.Movies);
			}
		}

		private ObservableCollection<Movie> movies;
		public ObservableCollection<Movie> Movies
		{
			get
			{
				if (this.movies == null)
				{
					this.movies = this.GetMoviesForStudio("Paramount Pictures");
				}
				return this.movies;
			}
			set
			{
				if (this.movies != value)
				{
					this.movies = value;
					OnPropertyChanged(() => this.Movies);
				}
			}
		}

		private ObservableCollection<Movie> GetMoviesForStudio(string studio)
		{
			return new ObservableCollection<Movie>(this.allMovies.Where(x => x.Studio == studio));
		}

		private ObservableCollection<string> GetStudios()
		{
			return new ObservableCollection<string> { "Paramount Pictures" , "Universal Pictures" , "Metro Goldwyn Mayer" , "20th Century Fox" , "Columbia Pictures" , "Warner Brothers" , "Miramax Films" , "Lucas Films" , "Walt Disney" };
		}

